CREATE TABLE CONFIG (
    config_id int PRIMARY KEY,
    doi_date varchar(30) DEFAULT(''),
    doi_message text DEFAULT(''),
    doi_countdown boolean DEFAULT(FALSE),
    motd text DEFAULT(''),
    show_additional_content boolean DEFAULT(FALSE)
);

CREATE TABLE LINK (
    site varchar(64) PRIMARY KEY DEFAULT(''),
    link varchar(512) DEFAULT(''),
    svg text DEFAULT(''),
    active boolean DEFAULT(TRUE)
);

CREATE TABLE RELEASETYPE (
    id integer PRIMARY KEY AUTOINCREMENT NOT NULL,
    type varchar(64) DEFAULT('')
);

CREATE TABLE RELEASE (
    id integer PRIMARY KEY AUTOINCREMENT NOT NULL,
    name varchar(256) DEFAULT(''),
    description varchar(512) DEFAULT(''),
    type int DEFAULT(0),
    image varchar(512) DEFAULT(''),
    active boolean DEFAULT(TRUE),
    release_date varchar(30) DEFAULT(''),
    FOREIGN KEY (type) REFERENCES RELEASETYPE(id)
);

CREATE TABLE RELEASELINK (
    release int NOT NULL,
    site varchar(64) NOT NULL DEFAULT(''),
    link varchar(512) NOT NULL DEFAULT(''),
    PRIMARY KEY (release, site),
    FOREIGN KEY (release) REFERENCES RELEASE(id)
);

CREATE TABLE QUOTE (
    quote TEXT DEFAULT('')
);

CREATE TABLE MEMBER (
    id integer PRIMARY KEY AUTOINCREMENT NOT NULL,
    name varchar(64),
    role varchar(32),
    description text,
    image varchar(512)
);



